// Bắt đầu bằng cách nhận đầu vào từ người dùng
// và lưu nó vào một biến.
userInput = input("Vui lòng nhập văn bản của bạn: ")

// Tiếp theo, chuyển đổi văn bản của người dùng thành chữ thường
// và lưu kết quả vào một biến khác.
lowercaseText = userInput.lower()

// Cuối cùng, in kết quả ra.
print(lowercaseText)

// Mẹo: Bạn có thể kết hợp các bước này thành một dòng duy nhất!
// print(input("Vui lòng nhập văn bản của bạn: ").lower())

// Nhận câu từ người dùng
sentence = input()

// Thay thế mỗi khoảng trắng bằng '...'
// Phương thức replace(old, new) rất hữu ích ở đây.
modifiedSentence = sentence.replace(' ', '...')

// In kết quả đã sửa đổi
print(modifiedSentence)

def main():
    // Nhận văn bản từ người dùng
    userInput = input()
    // Gọi hàm convert và in kết quả của nó
    print(convert(userInput))

def convert(text):
    // Thay thế :) bằng 🙂
    text = text.replace(':)', '🙂')
    // Thay thế :( bằng 🙁
    text = text.replace(':(', '🙁')
    // Trả về chuỗi đã được sửa đổi hoàn toàn
    return text

// Chạy hàm main
main()

// Yêu cầu người dùng nhập khối lượng (m) và chuyển đổi nó thành số nguyên
mass_kg = int(input("m: "))

// Định nghĩa tốc độ ánh sáng (c) bằng mét trên giây
speed_of_light_ms = 300000000

// Tính năng lượng (E) theo công thức của Einstein
// E = m * c^2
energy_joules = mass_kg * (speed_of_light_ms ** 2)

// In kết quả năng lượng
print(f"E: {energy_joules}")

def main():
    dollars = dollars_to_float(input("How much was the meal? "))
    percent = percent_to_float(input("What percentage would you like to tip? "))
    tip = dollars * percent
    print(f"Leave ${tip:.2f}")

def dollars_to_float(d):
    // Xóa ký tự '$' ở đầu và chuyển đổi phần còn lại thành float
    return float(d.removeprefix('$'))

def percent_to_float(p):
    // Xóa ký tự '%' ở cuối, chuyển đổi thành float và chia cho 100
    return float(p.removesuffix('%')) / 100

main()
